import React, { useState, useEffect } from "react";
import database, { auth } from "../firebase";
import firebase from "firebase";

function Home({user}) {
  const [input, setinput] = useState("");
  const [values, setvalues] = useState([]);

  useEffect(() => {
    database
      .collection("users")
      .orderBy("timestamp", "desc")
      .onSnapshot((snapshop) =>
        setvalues(
          snapshop.docs.map((doc) => ({
            id: doc.id,
            data: doc.data(),
          }))
        )
      );
  }, []);

  function savetodatabase(value) {
    database.collection("users").add({
      name: value,
      timestamp: firebase.firestore.FieldValue.serverTimestamp(),
      surname: "Aksu",
    });
    setinput("");
  }

  function deletedata(value) {
    database.collection("users").doc(value).delete();
  }

  function Editdata(id,value) {
    database.collection("users").doc(id).set(
      {
        name:value,

      },{merge:true}
    )
  }

  return (
    <div>
      <h1>Hoşgeldiniz {user.displayName}</h1>
      <button onClick={() => auth.signOut()}>Çıkış Yap </button>
      <input
        type="text"
        placeholder="Lütfen bir isim giriniz"
        value={input}
        onChange={() => setinput(document.querySelector("input").value)}
      />
      <button disabled={!input} onClick={() => savetodatabase(input)}>
        Ekle
      </button>
      <div>
        {values.map((item) => (
          <ul>
            <li>
              {item.data.name}
              <div>
                <button onClick={() => deletedata(item.id)}>Delete</button>
                <button onClick={() => Editdata(item.id,"Hiçkimse")}>Edit</button>
              </div>
            </li>
          </ul>
        ))}
      </div>
    </div>
  );
}

export default Home;
