import { NavLink } from "react-router-dom";
import {
  Divider,
  Grid,
  Menu,
  Image,
  Segment,
  Sticky,
  Table,
  Ref,
  Visibility,
} from 'semantic-ui-react'
export default function () {
    const border = {
        borderStyle: "solid",
        borderColor: "purple",
        borderRadius: "20px",
      }; 
    
      const baslikstyle=
      {
        fontSize:"20px",
        textAlign:"centered",
        color:"purple"
      };
    
      const maddestyle=
      {
        fontSize:"15px",
        backgroundColor:"white",
        borderColor:"black"
      };
      const headerback=
      {
        textDecoration:"none",
        marginTop:"3px",
        marginBottom:"0px",
        paddingBottom:"0px",
      };
    
      const ratingStyle={
          fontSize:"23px"
      }

    return (
        <Ref >
        <Grid >
          <Grid.Column width={4}>
            <Visibility >
              <Segment>
              <Menu text vertical style={maddestyle}>
          <Menu.Item  header style={baslikstyle}> <b>İlgili Kategoriler</b> </Menu.Item>
        
