        />
      </Menu>
      <Menu text vertical style={maddestyle}>
          <Menu.Item  header style={baslikstyle}> <b>İlgili Kategoriler</b> </Menu.Item>
        
        <Menu.Item
          name='closest'
          as={NavLink}
          to="/computers"
          // active={activeItem === 'closest'}
