        />
      </Menu>
              </Segment>
            </Visibility>
          </Grid.Column>

          <Grid.Column width={12}>
            <Sticky >
              <Table celled>
                <Table.Header>
                  <Table.Row>
                    <Table.HeaderCell>Calculation</Table.HeaderCell>
                    <Table.HeaderCell>Value</Table.HeaderCell>
                  </Table.Row>
                </Table.Header>
                <Table.Body>
                  <Table.Row>
                    <Table.Cell>topVisible</Table.Cell>
