export const ComputersPage = () => {
  const { products } = useContext(ComputerProductsContext);
  console.log(products);

  return (

    <Grid divided='vertically'>
    <Grid.Row columns={2}>
       
    </Grid.Row>

    <Grid.Row columns={3}>
    <>
      {products.length !== 0 && <h1>Bilgisayarlar</h1>}
      <div className="products-container">
        {products.length === 0 && <div>LÃ¼tfen Bekleyiniz</div>}
        {products.map((product) => (
          <div className="product-card" key={product.ProductID}>
            <div className="product-img">
              <img src={product.ProductImg} alt="not found" />
            </div>
            <div className="product-name">{product.ProductName}</div>
            <div className="product-explain"></div>
            <div className="product-price"> {product.ProductPrice}.00 TL</div>
            <button className="addcart-btn">Sepete Ekle</button>
          </div>
        ))}
      </div>
      </>
      </Grid.Row>
  </Grid>

  
  );
};
