                </Menu.Item>
                <Menu.Item>
                  <Checkbox label={<label>Mackbook</label>} />
                </Menu.Item>
                <Menu.Item>
                  <Checkbox label={<label>Lenovo</label>} />
                </Menu.Item>
                <Menu.Item>
                  <Checkbox label={<label>Monster</label>} />
                </Menu.Item>
              </Menu>

              <br />
              <Menu text vertical style={maddestyle}>
                <Menu.Item header style={baslikstyle}>
                  <b>Fiyat</b>
                </Menu.Item>
                <Form>
                  <Form.Field></Form.Field>
                  <Form.Field>
                    <Checkbox
                      radio
                      label="0 TL - 4500 TL"
                      name="checkboxRadioGroup"
                      value="bir"
                      checked={this.state.value === "bir"}
                      onChange={this.handleChange}
                    />
                  </Form.Field>
                  <Form.Field>
                    <Checkbox
                      radio
                      label="4500 TL - 8000 TL"
                      name="checkboxRadioGroup"
                      value="iki"
                      checked={this.state.value === "iki"}
                      onChange={this.handleChange}
                    />
                  </Form.Field>
                  <Form.Field>
                    <Checkbox
                      radio
                      label="8000 TL - 12500 TL"
                      name="checkboxRadioGroup"
                      value="uc"
                      checked={this.state.value === "uc"}
                      onChange={this.handleChange}
                    />
                  </Form.Field>
                  <Form.Field>
                    <Checkbox
                      radio
                      label="12500 TL - 17500 TL"
                      name="checkboxRadioGroup"
                      value="dort"
                      checked={this.state.value === "dort"}
                      onChange={this.handleChange}
                    />
                  </Form.Field>
                  <Form.Field>
                    <Checkbox
                      radio
                      label="17500 TL - 35000 TL"
                      name="checkboxRadioGroup"
                      value="bes"
                      checked={this.state.value === "bes"}
                      onChange={this.handleChange}
                    />
                  </Form.Field>
                </Form>
              </Menu>
            </Visibility>
          </Grid.Column>

          <Grid.Column width={12}>
            <Table>
              <Table.Body>
                <Table.Row>
                  <Table.Cell>
                    <Card style={cardStyle}>
                      <Header textAlign="right" style={headerback}>
                        <Rating
                          as="h1"
                          style={ratingStyle}
                          icon="heart"
                          defaultRating={0}
                          maxRating={1}
                        ></Rating>
                      </Header>
                      {/* <Button as={NavLink} to="/computers"style={buttonstyle}><Icon  style={iconstyle} name="like"/></Button> */}
                      <Image
                        style={imagestyle}
                        as={NavLink}
                        to="/computerDetailPages"
                        src="images/computers/monster.png"
                        wrapped
                        ui={false}
                      />
                      <Card.Content>
                        <Card.Header as={NavLink} to="/computerDetailPages">
                          MONSTER
                        </Card.Header>
                        <Card.Meta style={ozelliklerStyle}>
                          <span>
                            Abra A7 V13.2.3 Intel Core I5 11400h 16gb 500gb Ssd
                            Rtx3050ti Freedos 17.3'' Fhd ABRA A7 V13.2.3
                          </span>
                        </Card.Meta>
                        <Card.Description>
                          <Rating icon="star" defaultRating={3} maxRating={5} />
                          <br />
                          <span>
                            <h2>17.099 TL </h2>
                          </span>
                        </Card.Description>
                      </Card.Content>
                      <Card.Content extra>
                        <div style={border}>
                          <strong> Sepette %6 indirim 16.073 TL</strong>
                        </div>
                      </Card.Content>
                    </Card>
                  </Table.Cell>
                </Table.Row>
              </Table.Body>
            </Table>
          </Grid.Column>
        </Grid>
      </Ref>
    );
  }
