      setSurname("");
  };

  return (
    <div>
      <form>
        Ad :
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        Soyad :
        <input
          type="text"
          value={surname}
          onChange={(e) => setSurname(e.target.value)}
        />
        <Button onClick={add}>Ekle</Button>
      </form>
    </div>
  );
}

export default KayitOl;
