const SignUp = () => {
  

  const containerstyle = {
    width: "300px",
    margin: "15px 50px 10px 700px ",
    backgroundColor: "#FFFFFF",
    height: "350px",
    borderStyle: "solid",
    borderColor: "#800080",
  };

