import React, { useState, useEffect } from "react";
import Form from "react-bootstrap/Form";
import { NavLink } from "react-router-dom";
import { Button, Container, Icon } from "semantic-ui-react";
import { auth, provider } from "../firebase";
import "./Login.css";

const SignUp = () => {
  

  const containerstyle = {
    width: "300px",
    margin: "15px 50px 10px 700px ",
    backgroundColor: "#FFFFFF",
    height: "350px",
    borderStyle: "solid",
    borderColor: "#800080",
  };

  const girisyapstyle = {
    color: "black",
  };
  const buttonn = {
    backgroundColor: "#800080",
  };

  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [emailError, setEmailError] = useState("");
  const [passwordError, setPasswordError] = useState("");
  return (
    <Container style={containerstyle}>
      <Form onSubmit={handleSubmit}>
        <Form.Group style={girisyapstyle}>
          <h1>
            <b>-Kayıt Ol</b>
          </h1>
        </Form.Group>
        <br />
        <Form.Group style={girisyapstyle} size="lg">
        <h6>
            <b>Adı:</b>
          </h6>
          <Form.Control
            type="text"
            autoFocus
            value={firstName}
            required
            onChange={(e) => setFirstName(e.target.value)}
          />
          <h6>
            <b>Soyadı:</b>
          </h6>
          <Form.Control
            type="text"
            autoFocus
            value={lastName}
            required
            onChange={(e) => setLastName(e.target.value)}
          />
         

         
          <h6>
            <b>E-MAİL</b>
          </h6>
          <Form.Control
            type="text"
            autoFocus
            type="email"
            value={email}
            required
            onChange={(e) => setEmail(e.target.value)}
          />
          <p>{emailError}</p>
        </Form.Group>
        <br />
        <Form.Group style={girisyapstyle} size="lg" controlId="password">
          <h6>
            <b>ŞİFRE</b>
          </h6>

          <Form.Control
            type="password"
            required
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />

          <p>{passwordError}</p>
        </Form.Group>
        <br />
        <div>
          <Button onClick={handleLogin}>Kayıt Ol</Button>
          <p>
             Bir hesabın var mı?
            <Button  as={NavLink}
                to="/loginAndSignUpPage">
              Giriş Yap
            </Button>
          </p>
        </div>
        <br />
      </Form>
      <br />
    </Container>
  );
};
export default SignUp;





















