import React, { useState, useEffect } from "react";
import database from "../firebase";
import firebase from "firebase";

function Home() {
  const [input, setinput] = useState("");
  const [values, setvalues] = useState([]);

  useEffect(() => {
    database
      .collection("users")
      .orderBy("timestamp", "desc")
      .onSnapshot((snapshop) =>
        setvalues(
          snapshop.docs.map((doc) => ({
            id: doc.id,
            data: doc.data(),
          }))
        )
      );
  }, []);

  function savetodatabase(value,value2) {
    database.collection("users").add({
      name: value,
      timestamp: firebase.firestore.FieldValue.serverTimestamp(),
      surname: value2,
    });
    setinput(" ");
  }

  function deletedata(value) {
    database.collection("users").doc(value).delete();
  }

  return (
    <div>
      <input
        type="text"
        placeholder="LÃ¼tfen bir isim giriniz"
        value={input}
        onChange={() => setinput(document.querySelector("input").value)}
      />
      <button disabled={!input} onClick={() => savetodatabase(input)}>
        Ekle
      </button>
      <div>
        {values.map((item) => (
          <ul>
            <li>
              {item.data.name}
              <div>
                <button onClick={() => deletedata(item.id)}>Delete</button>
              </div>
            </li>
          </ul>
        ))}
      </div>
    </div>
  );
}

export default Home;
