import React, { useState, useEffect } from "react";
import database  from "../firebase";
import firebase from "firebase";
import { Button } from "semantic-ui-react";
import { NavLink } from "react-router-dom";

const Home= ({handleLogout})=> {
  const [input, setinput] = useState("");
  const [values, setvalues] = useState([]);
  const [name, setName] = useState("");
  const [surname, setSurname] = useState("");

  useEffect(() => {
    database
      .collection("users")
      .orderBy("timestamp", "desc")
      .onSnapshot((snapshop) =>
        setvalues(
          snapshop.docs.map((doc) => ({
            id: doc.id,
            data: doc.data(),
          }))
        )
      );
  }, []);

  function savetodatabase(value) {
    database.collection("users").add({
      name: value,
      timestamp: firebase.firestore.FieldValue.serverTimestamp(),
      surname: "Aksu",
    });
    setinput("");
  }

  function deletedata(value) {
    database.collection("users").doc(value).delete();
  }

  function Editdata(id,value) {
    database.collection("users").doc(id).set(
      {
        name:value,

      },{merge:true}
    )
  }

  console.log();
  const add = (e) => {
    e.preventDefault(); //sayfa yenilenmesin diye uaptık
    database.collection("users").add({
      name: name,
      surname: surname,
      timestamp: firebase.firestore.FieldValue.serverTimestamp(),
    });
    setName("");
    setSurname("");
  };

  return (
    <div>
      {/* <h1>Hoşgeldiniz {user.displayName}</h1> */}
      {/* <button onClick={() => auth.signOut()}>Çıkış Yap </button> */}
      <form>
         Ad :
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
          Soyad :
        <input
          type="text"
          value={surname}
          onChange={(e) => setSurname(e.target.value)}
        />
        <Button onClick={add}>Ekle</Button>
      </form>
      
      <button disabled={!input} onClick={() => savetodatabase(input)}>
        Ekle
      </button>
      <div>
        {values.map((item) => (
          <ul>
            <li>
              {item.data.name}
              <div>
                <button onClick={() => deletedata(item.id)}>Delete</button>
                <button onClick={() => Editdata(item.id,"Hiçkimse")}>Edit</button>
              </div>
            </li>
          </ul>
        ))}
      </div>

      <Button as={NavLink} to="KayitOl">
        Kayıt Ol 
      </Button>
    <Button onClick={handleLogout}>Çıkış Yap</Button>
  
    </div>
  );
}

export default Home;


