import React, {createContext} from 'react';
import database from '../../firebase';

export const ComputerProductsContext = createContext();

export class ComputerProductsContextProvider extends React.Component {
 
    //defining an innitial state with empty array of products
    state=
    {
      products : []
    }

    componentDidMount()
    {
     const prevProducts= this.state.products;
     database.collection('Computers').onSnapshot(snapshot=>{
         let changes = snapshot.docChanges();
         changes.forEach(change=>
            {
                if(change.type==='added')
                {
                    prevProducts.push({
                        ProductID: change.doc.id,
                        ProductName: change.doc.data().ProductName,
                        ProductPrice: change.doc.data().ProductPrice,
                        ProductImg:change.doc.data().ProductImg,
                    })
                }
                this.setState({
                    products:prevProducts
                })
            })
     })
    }

    render()
    {
        return(
          <ComputerProductsContext.Provider value={{products:[...this.state.products]}}>
              {this.props.children}
          </ComputerProductsContext.Provider>
        )
    }


};
