import React, { useState, useEffect } from "react";
import database from "../firebase";
import firebase from "firebase";

function Home() {
  const [values, setvalues] = useState([]);
  
  // useEffect(() => {
  //   database.collection("users").onSnapshot((snapshop) =>
  //     setvalues(
  //       snapshop.docs.map(doc => ({
  //         id: doc.id,
  //         data: doc.data(),
  //       }))
  //     )
  //   );
  // }, []);

  firebase
  .firestore()
  .collection("users")
  .onSnapshot((snapshot) => {
    const data = snapshot.docs.map((doc) => ({
      id: doc.id,
      ...doc.data(),
    }));
    console.log("All data in 'books' collection", data);
  });

  return (
    <div>
      <header>
        {values.map((item) => {
          <ul>
            <li>{item.data.name}</li>
          </ul>;
        })}
      </header>
    </div>
  );
}

export default Home;
