import React, {useState,useEffect} from 'react'
import database from "./firebase";
import firebase from "firebase"


 function home() {


  const[values,setvalues]=useState([]);


  useEffect(()=>
  {
     database.collection("users").onSnapshot(snapshop=>setvalues(snapshop.docs.map(doc=>(
       {
         id:doc.id, data:doc.data()
       } )) )) },[])

  return (
    <div>
      
       {
         values.map(item=>{<ul><li>{item.data.name}</li></ul>})
       }
    </div>
  )
}

export default home;
