    const border = {
        borderStyle: "solid",
        borderColor: "purple",
        borderRadius: "20px",
      }; 
    
      const baslikstyle=
      {
        fontSize:"20px",
        textAlign:"centered",
        color:"purple"
      };
    
      const maddestyle=
      {
        fontSize:"15px",
        backgroundColor:"white",
        borderColor:"black"
      };
      const headerback=
      {
        textDecoration:"none",
        marginTop:"3px",
        marginBottom:"0px",
        paddingBottom:"0px",
      };
    
      const ratingStyle={
          fontSize:"23px"
      }

