          name='mostPopular'
          as={NavLink}
          to="/computers"
          // active={activeItem === 'mostPopular'}
          // onClick={this.handleItemClick}
        />
      </Menu>
      <Menu text vertical style={maddestyle}>
          <Menu.Item  header style={baslikstyle}> <b>İlgili Kategoriler</b> </Menu.Item>
        
        <Menu.Item
          name='closest'
          as={NavLink}
          to="/computers"
          // active={activeItem === 'closest'}
          // onClick={this.handleItemClick}
        />
        <Menu.Item
          name='mostComments'
          as={NavLink}
          to="/computers"
          // active={activeItem === 'mostComments'}
          // onClick={this.handleItemClick}
        />
        <Menu.Item
          name='mostPopular'
          as={NavLink}
          to="/computers"
          // active={activeItem === 'mostPopular'}
          // onClick={this.handleItemClick}
        />
      </Menu>
      <Menu text vertical style={maddestyle}>
          <Menu.Item  header style={baslikstyle}> <b>İlgili Kategoriler</b> </Menu.Item>
        
        <Menu.Item
          name='closest'
          as={NavLink}
          to="/computers"
          // active={activeItem === 'closest'}
          // onClick={this.handleItemClick}
        />
        <Menu.Item
          name='mostComments'
          as={NavLink}
          to="/computers"
          // active={activeItem === 'mostComments'}
