import React,{useState, useEffect} from "react";
import { auth } from "../firebase";
import Home from "../pages/Home";
import LoginScreen from './LoginScreen';

function LoginAndSİgnUpPage() {

    const [user, setUser]=useState(null);
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    // const [user, setUser] = useState("");
    const [emailError, setEmailError] = useState("");
    const [passwordError, setPasswordError] = useState("");
  



    useEffect(() => {
    auth.onAuthStateChanged((authUser) =>  //durum değişirse
    {
      if(authUser)//Doluysa siteye giriş yapabil
      {
        setUser(authUser) //giriş yapıldı yani login ekranına yönlendirildi.
      }else //değilse;
      {
        setUser(null) //giriş ekranında bekle
      }
    }) 
    }, [])



    

  const clearInputs=()=>
  {
    setEmail="";
    setPassword="";
  }

  const clearErrors=()=>
  {
    setPasswordError="";
    setEmailError="";
  }

  const handleLogin = () => {
    clearErrors();
    firebase
      .auth()
      .signInWithEmailAndPassword(email.password)
      .catch((err) => {
        switch (err.code) {
          case "auth/invalid-email":
          case "auth/user-disabled":
          case "auth/user-not-found":
            setEmailError(err.message);
          case "auth/wrong-password":
            setPasswordError(err.message);
            break;
        }
      });
  };


  const handleSignup = () => {
    clearErrors();
    firebase
      .auth()
      .createUserWithEmailAndPassword(email.password)
      .catch((err) => {
        switch (err.code) {
          case "auth/email-already-in-use":
          case "auth/invalid-email":
            setEmailError(err.message);
          case "auth/weak-password":
            setPasswordError(err.message);
            break;
        }
      });
  };


  const handleLogout=()=>
  {
   firebase.auth().signOut();
  }



  const authListener=()=>
  {
    firebase.auth().onAuthStateChanged((user)=>
     {
       if(user)
       {
         clearInputs();
         setUser(user);
       }else
       {
         setUser("");
       }
     })
  }




  useEffect(()=>
  {
    authListener();
  },[])


  /* console.log(user); */
    return (
      <div className="app">
        {user ? <Home user={user}/> : <LoginScreen />} {/* /*setUser props olarak göndercem*/ }
      </div>
    );
}

export default LoginAndSİgnUpPage;
